import React, { useRef, useEffect, useState } from "react";
import "./Sections.css";
import myImage from "./assets/myImage.png";

import { BsTiktok } from "react-icons/bs";
import { BsFacebook } from "react-icons/bs";
import { BsInstagram } from "react-icons/bs";
import { BsTwitterX } from "react-icons/bs";
import { BsYoutube } from "react-icons/bs";


function Sections() {
  // Estado para manejar la visibilidad de la segunda sección
  const [showSecondSection, setShowSecondSection] = useState(false);
  // Referencia al elemento "sentinel" que usaremos como disparador
  const sentinelRef = useRef(null);

  useEffect(() => {
    // Creamos el Intersection Observer
    const observer = new IntersectionObserver(
      ([entry]) => {
        // Cuando el elemento sentinel esté 100% visible (threshold: 1.0),
        // significa que la primera sección ya se ubicó completamente arriba.
        if (entry.intersectionRatio === 1) {
          setShowSecondSection(true);
        } else {
          setShowSecondSection(false);
        }
      },
      { threshold: 1.0 }
    );

    // Observamos el sentinel
    if (sentinelRef.current) {
      observer.observe(sentinelRef.current);
    }

    // Limpieza del observer en el desmontaje del componente
    return () => {
      if (sentinelRef.current) {
        observer.unobserve(sentinelRef.current);
      }
    };
  }, []);

  return (
    <div className="sections-container">
      {/* Sección 1 */}
      <section className="section-one grey-border-section">
        <h2 className="section-title">
           About Me
        </h2>
        <div className="header-container">
          <div className="picture-section">
            <img src={myImage} alt="Descripción de la imagen" />
          </div>
          <div className="subsections-description">
            <div className="grey-border-section-top">
              <p>Venezuela, San Cristobal 2001 <span role="img" aria-label="emoticon">🔥</span></p>
              <p>
                Naci en San Cristobal y tengo 24 años, estoy interesado en seguir
                estudiando ingenieria informatica y telecomunicaciones.
              </p>
            </div>
            <div className="grey-border-section">
              <p>Enfoque <span role="img" aria-label="emoticon">🎯</span> </p>
              <p>
                Me gustaria enfocarme en areas como desarrollo web, blockchain y animaciones 3d en videojuegos.
              </p>
            </div>
            <div className="grey-border-section">
              <h5><span role="img" aria-label="emoticon">👍</span> Aspectos Positivos</h5>
              <p><span role="img" aria-label="emoticon">👪</span>Puedo trabajar en equipo fácilmente</p>
              <p><span role="img" aria-label="emoticon">🏅</span>Ambiente 100% remoto</p>
              <p><span role="img" aria-label="emoticon">📌</span>Puedo compilar, ejecutar y probar código</p>
              <p><span role="img" aria-label="emoticon">🍉</span>He utilizado herramientas como chat-gpt</p>
              <p><span role="img" aria-label="emoticon">💢</span>Administrador de redes sociales</p>
            </div>
          </div>
        </div>
        {/* Sentinela: elemento invisible para activar la transición */}
        <div ref={sentinelRef} className="sentinel"></div>
      </section>

      {/* Sección 2 */}
      <section className={`section-two ${showSecondSection ? "visible" : ""}`}>
    <div class="title-container">

    <ul class="suggestion-list">
            <li class="suggestion">
                <h4 class="text">Q&A support</h4>
                <span class="icon material-symbols-rounded">draw</span>
            </li>
            <li class="suggestion">
                <h3>Problemas de código y algoritmos <span role="img" aria-label="emoticon">👾</span> </h3>
      <p><a href="https://leetcode.com/u/prisco/">LeetCode</a></p>
      <p><a href="https://www.hackerrank.com/profile/jellous">HackerRank</a></p>
      <p><a href="https://www.codewars.com/users/hexorhex">CodeWars</a></p>
</li>
            <li class="suggestion">
                    <h3>Social Networks<span role="img" aria-label="emoticon">👨‍💻</span> </h3>
                    <BsTiktok /> <p>USER</p> 
                    <BsInstagram /><p>USER</p>
                    <BsFacebook /><p>USER</p>
                    <BsYoutube /><p>USER</p>
                    <BsTwitterX /><p>USER</p>

 </li>
            <li class="suggestion">
                <h3>Aprendizaje<span role="img" aria-label="emoticon">🌴</span> </h3>

 </li>
        </ul>


    
  </div>
</section>

    </div>
  );
}

export default Sections;
