import React, { useEffect, useRef } from "react";

function Header() {
  // Creamos una referencia para acceder al elemento canvas
  const canvasRef = useRef(null);

  useEffect(() => {
    // Obtenemos el canvas y el contexto 2D
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // Asignamos las dimensiones del canvas
    canvas.width = 500;
    canvas.height = 500;

    let angulo = 0;
    // Velocidades individuales para la rotación de cada cuadrado
    let velocidades = [0.01, -0.015, 0.02, -0.025];

    function dibujar() {
      // Limpiamos el lienzo
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Trasladamos el origen al centro del canvas
      ctx.translate(canvas.width / 2, canvas.height / 2);

      // Dibuja el círculo grande
      ctx.beginPath();
      ctx.arc(0, 0, 130, 0, Math.PI * 2);
      ctx.strokeStyle = `hsl(${(angulo * 50) % 360}, 100%, 50%)`;
      ctx.lineWidth = 4;
      ctx.stroke();

      // Dibuja tres cuadrados con rotaciones individuales
      for (let i = 0; i < 3; i++) {
        ctx.save(); // Guarda el estado actual del contexto
        ctx.rotate(angulo * velocidades[i]); // Aplica la rotación individual
        ctx.beginPath();
        let size = (3 - i) * 60; // Tamaño del cuadrado (de mayor a menor)
        ctx.rect(-size / 2, -size / 2, size, size);
        ctx.strokeStyle = `rgb(${i * 80}, ${255 - i * 80}, ${i * 100})`;
        ctx.lineWidth = 3;
        ctx.stroke();
        ctx.restore(); // Restaura el estado previo
      }

      // Restaura la transformación para el siguiente frame
      ctx.resetTransform();

      // Incrementa el ángulo para la próxima animación
      angulo += 0.02;
      requestAnimationFrame(dibujar);
    }

    dibujar();
  }, []); // Ejecutamos el efecto solo una vez

  return (
    <header>
      <div className="container">
        <div className="intro-text">
          <div className="intro-heading">Hello, mi nombre es Daniel Prisco.Soy nuevo en la programación y diseño web</div>
          <div className="intro-lead-in">
            Python • C++ • Java • SQL • JavaScript • CSS • HTML
          </div>
        </div>
          <canvas ref={canvasRef} id="miCanvas"></canvas>
      </div>
    </header>
  );
}

export default Header;
