import React, { useEffect, useRef } from "react";
import "./Sections.css";

function SuggestionsCarousel() {
  const listRef = useRef(null);

  useEffect(() => {
    const list = listRef.current;
    if (window.innerWidth < 768) { // Aplica solo en pantallas pequeñas
      const interval = setInterval(() => {
        list.scrollBy({ left: 250, behavior: "smooth" }); // Auto-scroll
      }, 3000);
      return () => clearInterval(interval); // Limpieza al desmontar
    }
  }, []);

  return (
    <div className="title-container">
      <ul ref={listRef} className="suggestion-list">
        <li className="suggestion">Item 1</li>
        <li className="suggestion">Item 2</li>
        <li className="suggestion">Item 3</li>
        <li className="suggestion">Item 4</li>
      </ul>
    </div>
  );
}

export default SuggestionsCarousel;
