import React, { useRef, useEffect, useState } from "react";
import "./Sections.css";
import myImage from "./assets/myImage.png";

function Sections() {
  // Estado para manejar la visibilidad de la segunda secci贸n
  const [showSecondSection, setShowSecondSection] = useState(false);
  // Referencia al elemento "sentinel" que usaremos como disparador
  const sentinelRef = useRef(null);

  useEffect(() => {
    // Creamos el Intersection Observer
    const observer = new IntersectionObserver(
      ([entry]) => {
        // Cuando el elemento sentinel est茅 100% visible (threshold: 1.0),
        // significa que la primera secci贸n ya se ubic贸 completamente arriba.
        if (entry.intersectionRatio === 1) {
          setShowSecondSection(true);
        } else {
          setShowSecondSection(false);
        }
      },
      { threshold: 1.0 }
    );

    // Observamos el sentinel
    if (sentinelRef.current) {
      observer.observe(sentinelRef.current);
    }

    // Limpieza del observer en el desmontaje del componente
    return () => {
      if (sentinelRef.current) {
        observer.unobserve(sentinelRef.current);
      }
    };
  }, []);

  return (
    <div className="sections-container">
      {/* Secci贸n 1 */}
      <section className="section-one grey-border-section">
        <h2 className="section-title">
          <span role="img" aria-label="emoticon"></span> About Me
        </h2>
        <div className="header-container">
          <div className="picture-section">
            <img src={myImage} alt="Descripci贸n de la imagen" />
          </div>
          <div className="subsections-description">
            <div className="grey-border-section-top">
              <p>Venezuela, San Cristobal 2001</p>
              <p>
                Naci en San Cristobal y tengo 24 a帽os, estoy interesado en seguir
                estudiando ingenieria informatica y telecomunicaciones.
              </p>
            </div>
            <div className="grey-border-section">
              <p>Enfoque</p>
              <p>
                Me gustaria enfocarme en areas como desarrollo web, blockchain y animaciones 3d en videojuegos.
              </p>
            </div>
            <div className="grey-border-section">
              <h5>Aspectos Positivos</h5>
              <p>Puedo trabajar en equipo f谩cilmente</p>
              <p>Ambiente 100% remoto</p>
              <p>Puedo compilar, ejecutar y probar c贸digo</p>
              <p>He utilizado herramientas como chat-gpt</p>
              <p>Administrador de redes sociales</p>
              <p>Y mucho mas...</p>
            </div>
          </div>
        </div>
        {/* Sentinela: elemento invisible para activar la transici贸n */}
        <div ref={sentinelRef} className="sentinel"></div>
      </section>

      {/* Secci贸n 2 */}
      <section className={`section-two ${showSecondSection ? "visible" : ""}`}>
        <h1 className="section-two-title ">Pruebas de c贸digo</h1>
        <p>
          Aqui os mostrare los sitios web frecuentemente visitados por mi.
        </p>
      </section>
    </div>
  );
}

export default Sections;
